add-function.inject-code:
        initializeNumpy();

        QVector<npy_intp> dimensions = {%CPPSELF.numReadoutPoints, %CPPSELF.numDimensions, %CPPSELF.numReadouts};
        QVector<npy_intp> strides = {};
        npy_intp stride = 1;
        for (const npy_intp dimension : dimensions) {
          strides.append(stride);
          stride *= dimension;
        }

        std::reverse(std::begin(dimensions), std::end(dimensions));
        %PYARG_0 = PyArray_NewFromDescr(&PyArray_Type, PyArray_DescrFromType(NPY_FLOAT32), 3, dimensions.data(), NULL, %CPPSELF.kSpaceCoordinates, NPY_ARRAY_WRITEABLE, NULL);
